#include <ignite/ignite.h>\n#include <ignite/ignition.h>\n\nint main() {\n    // Start Ignite node\n    IgniteConfiguration cfg;\n    Ignite ignite = Ignition::Start(cfg);\n\n    // Get cache instance\n    Cache<std::string, std::string> cache = ignite.GetCache<std::string, std::string>("myCache");\n\n    // Store data in cache\n    cache.Put("key", "value");\n\n    // Retrieve data from cache\n    std::string value = cache.Get("key");\n\n    return 0;\n}