// example.cpp\n#include <Windows.h>\n\n// Delay-load the DLL\nextern "C" __declspec(dllimport) void __stdcall MyFunction();\n\nint main() {\n    // Load the DLL on demand\n    HMODULE hModule = LoadLibraryEx("mydll.dll", NULL, LOAD_LIBRARY_AS_DATAFILE);\n    if (hModule) {\n        // Get the function pointer\n        FARPROC pMyFunction = GetProcAddress(hModule, "MyFunction");\n        if (pMyFunction) {\n            // Call the function\n            pMyFunction();\n        }\n        FreeLibrary(hModule);\n    }\n    return 0;\n}