#include <iostream>\n#include <vector>\n#include <fstream>\n#include <sstream>\nusing namespace std;\n\nint main(int arg_count, char *arg_vec[]) {\n    if (arg_count != 2) {\n        cerr << "expected one argument" << endl;\n        return EXIT_FAILURE;\n    }\n\n    string line;\n    ifstream myfile(arg_vec[1]);\n\n    if (myfile.is_open()) {\n        int count = 0;\n        string lastTag = "";\n\n        while (getline(myfile, line)) {\n            stringstream ss(line);\n            string Tag;\n\n            ss >> Tag; // read first column\n\n            if (Tag != lastTag) {\n                if (lastTag != "") {\n                    cout << lastTag << " " << count << endl;\n                }\n                lastTag = Tag;\n                count = 1;\n            } else {\n                count++;\n            }\n        }\n\n        // print the count for the last tag\n        if (lastTag != "") {\n            cout << lastTag << " " << count << endl;\n        }\n\n        myfile.close();\n    } else {\n        cout << "Unable to open file";\n    }\n    return 0;\n}