#include <variant>\n#include <iostream>\n\ntemplate <typename T>\nT add(T a, T b) {\n    return a + b;\n}\n\nint main() {\n    std::variant<float, double> value;\n\n    // Select float precision\n    value = 1.0f;\n    std::visit([](auto&& arg) { std::cout << add(arg, arg) << std::endl; }, value);\n\n    // Select double precision\n    value = 1.0;\n    std::visit([](auto&& arg) { std::cout << add(arg, arg) << std::endl; }, value);\n\n    return 0;\n}