#include <Windows.h>\n#include <Audioclient.h>\n\n// ...\n\nIAudioClient* pAudioClient;\nHRESULT hr;\n\n// Initialize COM\nCoInitialize(NULL);\n\n// Create an instance of the audio client\nhr = CoCreateInstance(__uuidof(AudioClient), NULL, CLSCTX_INPROC_SERVER, IID_PPV_ARGS(&pAudioClient));\nif (FAILED(hr)) {\n    // Handle error\n}\n\n// Get the current device ID\nAudioClientProperties props;\nhr = pAudioClient->GetProperties(&props);\nif (FAILED(hr)) {\n    // Handle error\n}\n\n// Set the new device ID\nprops.DefaultDevice = 1; // Replace with the ID of the device you want to switch to\nhr = pAudioClient->SetProperties(&props);\nif (FAILED(hr)) {\n    // Handle error\n}\n\n// Release resources\npAudioClient->Release();\nCoUninitialize();