#include <Windows.h>\n#include <mmsystem.h>\n\n// ...\n\nHWAVEOUT hWaveOut;\nWAVEHDR wh;\nMMRESULT mmResult;\n\n// Open the sound device\nmmResult = waveOutOpen(&hWaveOut, WAVE_MAPPER, &wh, 0, 0, CALLBACK_NULL);\nif (mmResult != MMSYSERR_NOERROR) {\n    // Handle error\n}\n\n// Get the current device ID\nDWORD dwCurrentDeviceID;\nmmResult = waveOutGetDevCaps(hWaveOut, &dwCurrentDeviceID, sizeof(dwCurrentDeviceID));\nif (mmResult != MMSYSERR_NOERROR) {\n    // Handle error\n}\n\n// Set the new device ID\nDWORD dwNewDeviceID = 1; // Replace with the ID of the device you want to switch to\nmmResult = waveOutSetPlayback(hWaveOut, dwNewDeviceID);\nif (mmResult != MMSYSERR_NOERROR) {\n    // Handle error\n}\n\n// Close the sound device\nwaveOutClose(hWaveOut);