// In your DLL file (e.g., hook.dll)\n#include <Windows.h>\n\nextern "C" __declspec(dllexport) LRESULT CALLBACK KeyboardProc(\n    int nCode, WPARAM wParam, LPARAM lParam)\n{\n    // Your keyboard hook logic goes here\n    return CallNextHookEx(NULL, nCode, wParam, lParam);\n}\n\n// In your main program file\n#include <Windows.h>\n\nint main()\n{\n    HMODULE hModule = LoadLibraryA("hook.dll");\n    if (hModule == NULL) {\n        // Error handling\n    }\n\n    HHOOK hook = SetWindowsHookEx(WH_KEYBOARD, (HOOKPROC)GetProcAddress(hModule, "KeyboardProc"), hModule, 0);\n    if (hook == NULL) {\n        // Error handling\n    }\n\n    MSG msg;\n    while (GetMessage(&msg, NULL, 0, 0) > 0) {\n        TranslateMessage(&msg);\n        DispatchMessage(&msg);\n    }\n\n    UnhookWindowsHookEx(hook);\n    FreeLibrary(hModule);\n    return 0;\n}