#include <QWebChannel>\n#include <QWebEnginePage>\n\nclass MyObject : public QObject {\n  Q_OBJECT\n  Q_PROPERTY(QString myProperty READ myProperty)\n\npublic:\n  MyObject(QObject* parent = nullptr) : QObject(parent) {}\n\n  QString myProperty() const {\n    return "Hello from C++!";\n  }\n};\n\nint main(int argc, char* argv[]) {\n  QApplication app(argc, argv);\n  QWebEnginePage page;\n  QWebChannel channel(&page);\n  channel.registerObject("myObject", new MyObject());\n  page.load(QUrl("file:///path/to/your/content.html"));\n  return app.exec();\n}