// mydll.cpp\n#include <Windows.h>\n\nclass MyClass {\npublic:\n    MyClass() {}\n    ~MyClass() { /* release resources here */ }\n};\n\nMyClass globalObj;\n\nextern "C" BOOL WINAPI DllMain(HINSTANCE hinstDLL, DWORD fdwReason, LPVOID lpvReserved)\n{\n    switch (fdwReason)\n    {\n    case DLL_PROCESS_ATTACH:\n        break;\n    case DLL_PROCESS_DETACH:\n        globalObj.~MyClass(); // Call destructor explicitly\n        break;\n    }\n    return TRUE;\n}