#include <vcl.h>\n#include <DBClient.hpp>\n#include <CSV.hpp>\n\n// Load CSV file into TClientDataSet when application starts\nvoid __fastcall TForm1::FormCreate(TObject *Sender)\n{\n    TClientDataSet* cds = new TClientDataSet(this);\n    cds->FileName = "data.csv";\n    cds->LoadFromFile();\n    cds->First();\n}\n\n// Save TClientDataSet to CSV file when application closes\nvoid __fastcall TForm1::FormClose(TObject *Sender, TCloseAction &Action)\n{\n    TClientDataSet* cds = (TClientDataSet*)DM->FindComponent("ClientDataSet1");\n    cds->SaveToFile("data.csv", dfCSV);\n}\n\n// Example of running a query on the TClientDataSet\nvoid __fastcall TForm1::Button1Click(TObject *Sender)\n{\n    TClientDataSet* cds = (TClientDataSet*)DM->FindComponent("ClientDataSet1");\n    cds->Filter = "Name = 'John'";\n    cds->Filtered = true;\n    cds->First();\n    while (!cds->Eof) {\n        // process data here\n        cds->Next();\n    }\n    cds->Filtered = false;\n}\n\n// Example of inserting a new record into the TClientDataSet\nvoid __fastcall TForm1::Button2Click(TObject *Sender)\n{\n    TClientDataSet* cds = (TClientDataSet*)DM->FindComponent("ClientDataSet1");\n    cds->Insert();\n    cds->FieldByName("Name")->AsString = "Jane";\n    cds->FieldByName("Age")->AsInteger = 30;\n    cds->Post();\n}\n\n// Example of deleting a record from the TClientDataSet\nvoid __fastcall TForm1::Button3Click(TObject *Sender)\n{\n    TClientDataSet* cds = (TClientDataSet*)DM->FindComponent("ClientDataSet1");\n    cds->Delete();\n}