#include <iostream>\n#include <vector>\n#include <string>\n#include <unordered_set>\n\nusing namespace std;\n\nvoid calcMain(int upperBound) {\n    unordered_set<int> known;\n    for (int i = 0; i <= upperBound; i++) {\n        int current = i;\n        vector<int> history;\n        while (true) {\n            string digits = to_string(current);\n            int squaresum = 0;\n            for (char digit : digits) {\n                squaresum += (digit - '0') * (digit - '0');\n            }\n            current = squaresum;\n            if (known.find(current) != known.end()) {\n                break;\n            }\n            history.push_back(current);\n            if (current == 1) {\n                known.insert(i);\n            }\n        }\n    }\n}\n\nint main() {\n    // ...\n}