#include <iostream>\n\nclass Example {\npublic:\n    int a, b, c, d;\n    char e;\n    // For demonstration, assuming padding and unsigned short int\n    unsigned short int f;\n\n    Example() : a(1), b(2), c(3), d(4), e('5'), f(6) {\n        std::cout << "Constructed" << std::endl;\n    }\n\n    ~Example() {\n        std::cout << "Destructed" << std::endl;\n    }\n\n    void doSomething() {\n        std::cout << "Doing something..." << std::endl;\n    }\n};\n\nint main() {\n    const int bufferSize = sizeof(Example);\n    char buffer[bufferSize];\n\n    // Construct the object in the buffer\n    Example* obj = new (buffer) Example;\n\n    // Use the object\n    obj->doSomething();\n\n    // Destroy the object\n    obj->~Example();\n\n    return 0;\n}