#include <Windows.h>\n\n// Assume the DLL name is "mydll.dll" and the function name is "myfunction"\nHMODULE hModule = LoadLibraryA("mydll.dll");\nif (hModule != NULL) {\n    FARPROC pFunction = GetProcAddress(hModule, "myfunction");\n    if (pFunction != NULL) {\n        // Call the function using a function pointer\n        typedef void (*MYFUNCTION)();\n        MYFUNCTION pMyFunction = (MYFUNCTION)pFunction;\n        pMyFunction();\n    }\n    FreeLibrary(hModule);\n}