#include <windows.h>\n#include <objbase.h>\n\nint main() {\n    // Initialize COM with the desired apartment state\n    HRESULT result = CoInitializeEx(nullptr, COINIT_MULTITHREADED);\n\n    if (SUCCEEDED(result)) {\n        // Your thread is now in the multithreaded apartment state\n\n        // Don't forget to uninitialize COM when you're done\n        CoUninitialize();\n    }\n\n    return 0;\n}