#include <tesseract/baseapi.h>\n\nint main() {\n    // Load the tesseract-ocr DLL\n    HMODULE hTessDll = LoadLibrary(TEXT("tesseract.dll"));\n    if (hTessDll == NULL) {\n        // Handle error\n    }\n\n    // Get the TessBaseAPI function pointer\n    typedef TessBaseAPI* (*TESS_BASE_API_PTR)();\n    TESS_BASE_API_PTR tessBaseApi = (TESS_BASE_API_PTR)GetProcAddress(hTessDll, "TessBaseAPI");\n    if (tessBaseApi == NULL) {\n        // Handle error\n    }\n\n    // Create a TessBaseAPI instance\n    TessBaseAPI* api = tessBaseApi();\n\n    // Use the TessBaseAPI instance\n    api->Init(NULL, "eng");\n    // ...\n\n    // Unload the tesseract-ocr DLL\n    FreeLibrary(hTessDll);\n    return 0;\n}