#include <iostream>\n#include <string>\n#include <bitset>\n\nvoid printBitRepresentation(const std::string& s) {\n    for (const char& c : s) {\n        std::bitset<8> bits(c); // Create a bitset from the character\n        std::cout << bits << " "; // Print the bit representation\n    }\n    std::cout << std::endl;\n}\n\nint main() {\n    std::string input = "\x80";\n    printBitRepresentation(input);\n    return 0;\n}