#include <wincrypt.h>\n\nint main() {\n    HCRYPTPROV hCryptProv;\n    HCRYPTHASH hCryptHash;\n    HCRYPTKEY hCryptKey;\n\n    // Initialize the crypto provider\n    CryptAcquireContext(&hCryptProv, NULL, NULL, PROV_RSA_FULL, 0);\n\n    // Create a hash object\n    CryptCreateHash(hCryptProv, CALG_DES, 0, 0, &hCryptHash);\n\n    // Create a key object\n    CryptDeriveKey(hCryptProv, CALG_DES, hCryptHash, 0, &hCryptKey);\n\n    // Encrypt data\n    BYTE* pData = (BYTE*)"Hello, World!";\n    DWORD dwDataLen = strlen((char*)pData);\n    BYTE* pEncryptedData = new BYTE[dwDataLen];\n    CryptEncrypt(hCryptKey, hCryptHash, TRUE, 0, pData, &dwDataLen, pEncryptedData);\n\n    // Clean up\n    CryptDestroyKey(hCryptKey);\n    CryptDestroyHash(hCryptHash);\n    CryptReleaseContext(hCryptProv, 0);\n\n    return 0;\n}