#include <Windows.h>\n#include <sms.h>\n\nint WINAPI WinMain(HINSTANCE hInstance, HINSTANCE hPrevInstance, LPSTR lpCmdLine, int nCmdShow)\n{\n    const TCHAR* message = TEXT("Hello, World!");\n    const TCHAR* recipientNumber = TEXT("+1234567890");\n    DWORD messageId = 0;\n\n    int result = SmsSendMessage(NULL, message, recipientNumber, SMS_SEND_MESSAGE_FLAG_REQUEST_STATUS, &messageId);\n\n    if (result == 0)\n    {\n        MessageBox(NULL, TEXT("Message sent successfully."), TEXT("Success"), MB_OK);\n    }\n    else\n    {\n        MessageBox(NULL, TEXT("Error sending message."), TEXT("Error"), MB_ICONERROR);\n    }\n\n    return 0;\n}