#include <iostream>\n#include <string>\n\nint main() {\n    std::string input;\n    while (true) {\n        std::cout << "prompt> ";\n        if (!std::getline(std::cin, input)) {\n            if (std::cin.eof()) {\n                std::cin.clear(); // Reset error flags\n            } else {\n                break; // Some other error occurred\n            }\n        } else if (!input.empty()) {\n            // Process input\n            std::cout << "You entered: " << input << std::endl;\n        }\n    }\n    return 0;\n}