#include <iostream>\n#include <fstream>\n#include <chrono>\n\nint main() {\n    const int numIterations = 1000000;\n    const char* filename = "log.txt";\n\n    // Test 1: Logging to file\n    std::ofstream file(filename);\n    auto startFile = std::chrono::high_resolution_clock::now();\n    for (int i = 0; i < numIterations; ++i) {\n        file << i << std::endl;\n    }\n    auto endFile = std::chrono::high_resolution_clock::now();\n    file.close();\n\n    // Test 2: No logging\n    auto startNoFile = std::chrono::high_resolution_clock::now();\n    for (int i = 0; i < numIterations; ++i) {\n        // Do nothing\n    }\n    auto endNoFile = std::chrono::high_resolution_clock::now();\n\n    std::cout << "Time with logging: "\n              << std::chrono::duration_cast<std::chrono::milliseconds>(endFile - startFile).count()\n              << " ms" << std::endl;\n    std::cout << "Time without logging: "\n              << std::chrono::duration_cast<std::chrono::milliseconds>(endNoFile - startNoFile).count()\n              << " ms" << std::endl;\n\n    return 0;\n}