#include <mongo/client.h>\n\n// Create a MongoDB client\nmongo::client::MongoClient client("mongodb://localhost:27017");\n\n// Insert a document\nmongo::bson::Document doc;\ndoc["guid"] = "guid-123";\ndoc["text"] = "Hello, World!";\nclient.insert("mycollection", doc);\n\n// Find a document\nmongo::bson::Document filter;\nfilter["guid"] = "guid-123";\nauto result = client.find("mycollection", filter);\nif (result) {\n    std::string retrieved_text = result->get("text").str();\n}