#include <iostream>\n#include <vector>\n\nstd::vector<int> getPrimes(int limit) {\n    std::vector<int> primes;\n    for (int i = 2; i <= limit; i++) {\n        bool isPrime = true;\n        for (int j = 2; j * j <= i; j++) {\n            if (i % j == 0) {\n                isPrime = false;\n                break;\n            }\n        }\n        if (isPrime) {\n            primes.push_back(i);\n        }\n    }\n    return primes;\n}\n\nint main() {\n    int limit = 10000;\n    std::vector<int> primes = getPrimes(limit);\n    for (int prime : primes) {\n        std::cout << prime << std::endl;\n    }\n    return 0;\n}