#include <Windows.h>\n#include <Wbemidl.h>\n\n#pragma comment(lib, "wbemuuid.lib")\n\nint main() {\n    IWbemServices* pSvc = NULL;\n    IWbemClassObject* pClass = NULL;\n    IWbemClassObject* pInstance = NULL;\n\n    // Initialize COM\n    CoInitialize(NULL);\n\n    // Connect to the WMI service\n    CoCreateInstance(CLSID_WbemAdministrativeLocator, NULL, CLSCTX_INPROC_SERVER, IID_IWbemServices, (LPVOID*)&pSvc);\n\n    // Get the AntiVirusProduct class\n    pSvc->GetObject(L"ROOT\\SecurityCenter2:AntiVirusProduct", 0, NULL, IID_IWbemClassObject, (LPVOID*)&pClass);\n\n    // Get the first instance of the AntiVirusProduct class\n    pClass->GetMethod(L"Instances_", 0, NULL, IID_IWbemClassObject, (LPVOID*)&pInstance);\n\n    // Get the properties of the instance\n    VARIANT var;\n    VariantInit(&var);\n    pInstance->Get(L"displayName", 0, NULL, &var, NULL, NULL);\n    wprintf(L"Antivirus display name: %s\n", var.bstrVal);\n    VariantClear(&var);\n\n    // Clean up\n    pInstance->Release();\n    pClass->Release();\n    pSvc->Release();\n    CoUninitialize();\n\n    return 0;\n}