#include <iostream>\n#include <fstream>\n#include <string>\n\nusing namespace std;\n\nint main() {\n    string filename;\n    cout << "Please enter the filename: ";\n    cin >> filename;\n\n    ifstream infile(filename.c_str());\n\n    if (!infile.is_open()) {\n        cout << "Unable to open file";\n        return 1;\n    }\n\n    char c;\n    while (infile.get(c)) {\n        cout.put(c);\n    }\n\n    infile.close();\n    getchar();\n    return 0;\n}