// Example usage:\n#include <soci.h>\n#include <soci/sqlite3/soci-sqlite3.h>\n\nint main() {\n    soci::session sql(soci::sqlite3, "db=mydatabase.db");\n    soci::rowset<soci::row> rs = (sql.prepare << "SELECT * FROM mytable");\n    for (soci::rowset<soci::row>::iterator i = rs.begin(); i != rs.end(); ++i) {\n        std::cout << "Name: " << (*i)["name"] << std::endl;\n    }\n    return 0;\n}