#include <iostream>\n#include <list>\n\n#define NUM_VERTICES 8\n\nusing namespace std;\n\nenum { WHITE, GRAY, BLACK };\n\nstruct vertex\n{\n    int color;\n    int distance;\n    char parent;\n};\n\nint main()\n{\n    // create the vertices\n    vertex r = {WHITE, NULL, NULL};\n\n    // create pointer to the vertex structures\n    vertex *pr = &r;\n\n    // create a list to hold the vertices\n    list<vertex*> r_list;\n\n    r_list.push_back(pr);\n\n    return 0;\n}