#include <Windows.h>\n#include <iostream>\n\nint main() {\n    HANDLE hToken;\n    TOKEN_ELEVATION_TYPE elevationType;\n\n    // Get the current process token\n    OpenProcessToken(GetCurrentProcess(), TOKEN_QUERY, &hToken);\n\n    // Check the elevation type\n    DWORD dwSize = sizeof(TOKEN_ELEVATION_TYPE);\n    GetTokenInformation(hToken, TokenElevationType, &elevationType, sizeof(elevationType), &dwSize);\n\n    if (elevationType == TokenElevationTypeFull) {\n        std::cout << "Running with elevated privileges (Local System)." << std::endl;\n    } else {\n        std::cout << "Running with limited privileges (Network Service)." << std::endl;\n    }\n\n    return 0;\n}