// Example usage:\n#include <cppdb.h>\n\nstruct User {\n    int id;\n    std::string name;\n};\n\nint main() {\n    cppdb::session db("sqlite3:mydatabase.db");\n    cppdb::query q = db.query();\n    q << "SELECT * FROM users";\n    cppdb::result r = q.execute();\n    while (r.next()) {\n        User user;\n        r >> user.id >> user.name;\n        std::cout << "Name: " << user.name << std::endl;\n    }\n    return 0;\n}