#include <Windows.h>\n#include <Security.h>\n\nint main() {\n    HANDLE hToken;\n    DWORD dwBufferSize = 0;\n    PTOKEN_GROUPS pGroups = NULL;\n\n    // Open the current process token\n    OpenProcessToken(GetCurrentProcess(), TOKEN_QUERY, &hToken);\n\n    // Get the buffer size required for the group information\n    GetTokenInformation(hToken, TokenGroups, NULL, 0, &dwBufferSize);\n\n    // Allocate the buffer for the group information\n    pGroups = (PTOKEN_GROUPS)malloc(dwBufferSize);\n\n    // Get the group information\n    GetTokenInformation(hToken, TokenGroups, pGroups, dwBufferSize, &dwBufferSize);\n\n    // Print the group names\n    for (DWORD i = 0; i < pGroups->GroupCount; i++) {\n        SID_NAME_USE SidNameUse;\n        TCHAR szGroupName[256];\n        DWORD dwGroupNameSize = 256;\n\n        // Get the group name from the SID\n        LookupAccountSid(NULL, pGroups->Groups[i].Sid, szGroupName, &dwGroupNameSize, NULL, NULL, &SidNameUse);\n\n        printf("%s\n", szGroupName);\n    }\n\n    // Free the buffer and close the token handle\n    free(pGroups);\n    CloseHandle(hToken);\n\n    return 0;\n}