#include <boost/regex.hpp>\n\nstd::string html = "<div class=\"tab news selected\"><a href=\"#\">Link 1</a><a href=\"#\">Link 2</a></div>";\n\nboost::regex regex("<div\\s+class\\s*=\\s*\"tab news selected\"[^>]*>(.*?)</div>", boost::regex_constants::dot_matches_newlines);\n\nboost::smatch match;\nif (boost::regex_search(html, match, regex)) {\n    std::cout << "Matched: " << match[0].str() << std::endl;\n}