#include <iostream>\n#include <fstream>\n#include <string>\n#include <cstdlib>\n\nusing namespace std;\n\nint main(int argc, char* argv[]) {\n    if (argc != 2) {\n        cout << "Error syntax is incorrect!\nSyntax: " << argv[0] << " <file>\n";\n        return 0;\n    }\n\n    ifstream file(argv[1]);\n    if (!file.good()) {\n        cout << "File " << argv[1] << " does not exist.\n";\n        return 0;\n    }\n\n    string linha;\n    while (!file.eof()) {\n        getline(file, linha);\n        if (linha.find("print") != string::npos) {\n            size_t start = linha.find('"') + 1;\n            size_t end = linha.find_last_of('"');\n            string text = linha.substr(start, end - start);\n            cout << text << endl;\n        }\n    }\n    return 0;\n}