#include "ns3/core-module.h"\n#include "ns3/network-module.h"\n#include "ns3/internet-module.h"\n#include "ns3/point-to-point-module.h"\n\nusing namespace ns3;\n\nint main() {\n  // Create a grid topology with 100 nodes\n  PointToPointGridHelper grid(10, 10);\n\n  // Configure the network conditions\n  PointToPointHelper p2p;\n  p2p.SetDeviceAttribute("DataRate", DataRate(1e6)); // 1 Mbps\n  p2p.SetChannelAttribute("Delay", Time(10)); // 10 ms delay\n\n  // Create the network\n  InternetStackHelper internet;\n  internet.Install(grid.GetNodes());\n\n  // Run the simulation\n  Simulator::Run();\n  Simulator::Destroy();\n\n  return 0;\n}