#include <sql.h>\n#include <sqlext.h>\n\nint main() {\n    // Connect to the database\n    SQLHENV env;\n    SQLHDBC dbc;\n    SQLHSTMT stmt;\n    SQLRETURN ret;\n\n    ret = SQLAllocHandle(SQL_HANDLE_ENV, SQL_NULL_HANDLE, &env);\n    ret = SQLSetEnvAttr(env, SQL_ATTR_ODBC_VERSION, (SQLPOINTER)SQL_OV_ODBC3, 0);\n    ret = SQLAllocHandle(SQL_HANDLE_DBC, env, &dbc);\n    ret = SQLConnect(dbc, (SQLCHAR *)"mysql_database", SQL_NTS, (SQLCHAR *)"mysql_user", SQL_NTS, (SQLCHAR *)"mysql_password", SQL_NTS);\n\n    // Execute a query\n    ret = SQLAllocHandle(SQL_HANDLE_STMT, dbc, &stmt);\n    ret = SQLPrepare(stmt, (SQLCHAR *)"SELECT * FROM my_table", SQL_NTS);\n    ret = SQLExecute(stmt);\n\n    // Fetch the results\n    while (ret == SQL_SUCCESS) {\n        ret = SQLFetch(stmt);\n        if (ret == SQL_SUCCESS) {\n            SQLINTEGER id;\n            ret = SQLGetData(stmt, 1, SQL_INTEGER, &id, 0, NULL);\n            std::cout << "ID: " << id << std::endl;\n        }\n    }\n\n    return 0;\n}