#include <iostream>\n\nclass Stack {\nprivate:\n    int* arr;\n    int top;\n    int capacity;\n\npublic:\n    Stack(int capacity) {\n        this->capacity = capacity;\n        arr = new int[capacity];\n        top = -1;\n    }\n\n    ~Stack() {\n        delete[] arr;\n    }\n\n    void push(int value) {\n        if (top == capacity - 1) {\n            std::cerr << "Stack overflow!" << std::endl;\n            return;\n        }\n        arr[++top] = value;\n    }\n\n    int pop() {\n        if (top == -1) {\n            std::cerr << "Stack underflow!" << std::endl;\n            return -1; // or throw an exception\n        }\n        return arr[top--];\n    }\n\n    int peek() {\n        if (top == -1) {\n            std::cerr << "Stack is empty!" << std::endl;\n            return -1; // or throw an exception\n        }\n        return arr[top];\n    }\n};