#include <string>\n#include <openssl/aes.h>\n\n// Assume 'encryptedData' is the encrypted data from storage\nstd::string encryptedData = "...";\n\n// Decrypt the data\nstd::string decryptedData;\n{\n    AES_KEY aesKey;\n    // Initialize the AES key\n    AES_set_encrypt_key((const unsigned char*)"your_secret_key", 128, &aesKey);\n    // Decrypt the data\n    unsigned char* decryptedBytes = new unsigned char[encryptedData.size()];\n    AES_decrypt((const unsigned char*)encryptedData.c_str(), decryptedBytes, &aesKey);\n    decryptedData = std::string((char*)decryptedBytes, encryptedData.size());\n    delete[] decryptedBytes;\n}\n\n// Use the decrypted data\n// ...\n\n// Immediately recode (encrypt) the data\nstd::string encryptedDataAgain;\n{\n    AES_KEY aesKey;\n    // Initialize the AES key\n    AES_set_encrypt_key((const unsigned char*)"your_secret_key", 128, &aesKey);\n    // Encrypt the data\n    unsigned char* encryptedBytes = new unsigned char[decryptedData.size()];\n    AES_encrypt((const unsigned char*)decryptedData.c_str(), encryptedBytes, &aesKey);\n    encryptedDataAgain = std::string((char*)encryptedBytes, decryptedData.size());\n    delete[] encryptedBytes;\n}