#include <cppdb/driver_manager.h>\n#include <cppdb/transaction.h>\n\nint main() {\n    // Connect to the database\n    cppdb::driver_manager::instance().register_driver("mysql");\n    cppdb::connection conn = cppdb::driver_manager::instance().connect("mysql://mysql_database:mysql_user:mysql_password@localhost/my_table");\n\n    // Create a transaction\n    cppdb::transaction trx(conn);\n\n    // Execute a query\n    cppdb::result res = trx.select("SELECT * FROM my_table");\n\n    // Iterate over the results\n    for (cppdb::result::iterator i = res.begin(); i != res.end(); ++i) {\n        std::cout << "ID: " << (*i)["id"] << std::endl;\n    }\n\n    return 0;\n}