// Native DLL (C++/CLI)\n#pragma managed(push, off)\n#include <SketchupAPI.h>\n#pragma managed(pop)\n\npublic ref class SketchupInterop\n{\npublic:\n    SketchupInterop()\n    {\n        // Initialize Sketchup API\n        SUInitialize();\n    }\n\n    ~SketchupInterop()\n    {\n        // Clean up Sketchup API\n        SUTerminate();\n    }\n\n    void CreateModel()\n    {\n        // Call a Sketchup API method\n        SUMakeModel();\n    }\n};