#include <iostream>\n#include <chrono>\n#include <thread>\n\nint main() {\n    int progress = 0;\n    while (progress <= 100) {\n        std::cout << "\rProgress: " << progress << "%" << std::flush;\n        std::this_thread::sleep_for(std::chrono::milliseconds(50));\n        progress++;\n    }\n    std::cout << std::endl; // Move to next line after progress is complete\n    return 0;\n}