#include <csignal>\n#include <iostream>\n\nvoid cleanup() {\n    // Close files and print stuff here\n    std::cout << "Cleaning up..." << std::endl;\n    // ...\n}\n\nvoid signalHandler(int sig) {\n    if (sig == SIGINT) {\n        cleanup();\n        exit(0);\n    }\n}\n\nint main() {\n    signal(SIGINT, signalHandler);\n    // Your program code here\n    while (true) {\n        // ...\n    }\n    return 0;\n}