#include <Windows.h>\n#include <wincred.h>\n\n// ...\n\n// In the handleProxyAuthentication slot:\nvoid YourClass::handleProxyAuthentication(const QNetworkProxy &proxy, QAuthenticator *authenticator)\n{\n    // Retrieve the current user's credentials using the Windows API\n    PCREDENTIALW credentials;\n    DWORD count = 0;\n    if (CredEnumerate(NULL, 0, &count, &credentials) == TRUE) {\n        for (DWORD i = 0; i < count; i++) {\n            if (credentials[i].Type == CRED_TYPE_GENERIC) {\n                // Use the retrieved credentials to authenticate with the proxy\n                authenticator->setUser(credentials[i].UserName);\n                authenticator->setPassword(credentials[i].CredentialBlob);\n                break;\n            }\n        }\n        CredFree(credentials);\n    }\n}