#include <Windows.h>\n#include <iostream>\n\nint main() {\n    HANDLE hDir = CreateFileA(".", FILE_LIST_DIRECTORY, FILE_SHARE_READ | FILE_SHARE_WRITE | FILE_SHARE_DELETE, NULL, OPEN_EXISTING, FILE_FLAG_BACKUP_SEMANTICS, NULL);\n    if (hDir == INVALID_HANDLE_VALUE) {\n        std::cerr << "Error opening directory" << std::endl;\n        return 1;\n    }\n\n    FILE_NOTIFY_INFORMATION buffer[1024];\n    DWORD bytesReturned;\n    while (ReadDirectoryChangesW(hDir, buffer, sizeof(buffer), TRUE, FILE_NOTIFY_CHANGE_LAST_WRITE, &bytesReturned, NULL, NULL)) {\n        for (int i = 0; i < bytesReturned / sizeof(FILE_NOTIFY_INFORMATION); i++) {\n            std::wcout << buffer[i].FileName << std::endl;\n        }\n    }\n\n    CloseHandle(hDir);\n    return 0;\n}