#include <Windows.h>\n\n// Define the hook procedure\nLRESULT CALLBACK FileSystemHookProc(int nCode, WPARAM wParam, LPARAM lParam)\n{\n    // Check if a file is being created\n    if (nCode == HCBT_CREATEFILE)\n    {\n        // Get the file name\n        const char* fileName = (const char*)lParam;\n\n        // Check if it's the log file you're interested in\n        if (strcmp(fileName, "your_log_file.log") == 0)\n        {\n            // Inject a debug break to catch the call stack\n            DebugBreak();\n        }\n    }\n\n    // Pass the event to the next hook in the chain\n    return CallNextHookEx(NULL, nCode, wParam, lParam);\n}\n\nint main()\n{\n    // Set up the file system hook\n    HHOOK hHook = SetWindowsHookEx(WH_CBT, FileSystemHookProc, NULL, GetCurrentThreadId());\n\n    // Run your application as usual...\n\n    // Unhook when done\n    UnhookWindowsHookEx(hHook);\n    return 0;\n}