#include <iostream>\n#include <unordered_map>\n\nint findMaxFrequency(int arr[], int n) {\n    std::unordered_map<int, int> freqMap;\n    int maxFreq = 0;\n\n    for (int i = 0; i < n; i++) {\n        freqMap[arr[i]]++;\n        maxFreq = std::max(maxFreq, freqMap[arr[i]]);\n    }\n\n    return maxFreq;\n}\n\nint main() {\n    int arr[] = {2, 456, 34, 3456, 2, 435, 2, 456, 2};\n    int n = sizeof(arr) / sizeof(arr[0]);\n\n    int maxFreq = findMaxFrequency(arr, n);\n    std::cout << "Maximum frequency: " << maxFreq << std::endl;\n\n    return 0;\n}