#include <iostream>\n\nstruct Data {\n    int x;\n    float y;\n    char z;\n\n    friend std::ostream& operator<<(std::ostream& os, const Data& data) {\n        os << "x: " << data.x << std::endl;\n        os << "y: " << data.y << std::endl;\n        os << "z: " << data.z << std::endl;\n        return os;\n    }\n};\n\nint main() {\n    Data data = {10, 3.14f, 'A'};\n    std::cout << data << std::endl;\n    return 0;\n}