#include <Windows.h>\n\nint main() {\n    // Set up a file system watcher\n    HANDLE hDir = CreateFileW(L"C:\\Path\\To\\Watch", FILE_LIST_DIRECTORY,\n        FILE_SHARE_READ | FILE_SHARE_WRITE | FILE_SHARE_DELETE, NULL,\n        OPEN_EXISTING, FILE_FLAG_BACKUP_SEMANTICS, NULL);\n\n    if (hDir != INVALID_HANDLE_VALUE) {\n        FILE_NOTIFY_INFORMATION buffer[1024];\n        DWORD bytesReturned = 0;\n\n        while (ReadDirectoryChangesW(hDir, buffer, sizeof(buffer),\n            TRUE, FILE_NOTIFY_CHANGE_FILE_NAME | FILE_NOTIFY_CHANGE_LAST_WRITE,\n            &bytesReturned, NULL, NULL)) {\n            // Process the changes\n            for (DWORD i = 0; i < bytesReturned; i += buffer[i].NextEntryOffset) {\n                // Check the file name and action (e.g., created, deleted, modified)\n                printf("%ws: %d\n", buffer[i].FileName, buffer[i].Action);\n            }\n        }\n\n        CloseHandle(hDir);\n    }\n\n    return 0;\n}