#include <soci.h>\n#include <soci/mysql/soci-mysql.h>\n\nint main() {\n    // Connect to the database\n    soci::session sql(soci::mysql, "db=mysql_database user=mysql_user password=mysql_password");\n\n    // Execute a query\n    soci::rowset<soci::row> rs = (sql.prepare << "SELECT * FROM my_table");\n\n    // Iterate over the results\n    for (soci::rowset<soci::row>::iterator i = rs.begin(); i != rs.end(); ++i) {\n        std::cout << "ID: " << (*i)["id"] << std::endl;\n    }\n\n    return 0;\n}