#include <filesystem>\n#include <iostream>\n\nint main() {\n    namespace fs = std::filesystem;\n    fs::path path("/home/shree/path/def");\n\n    if (fs::is_directory(path)) {\n        std::cout << path << " is a directory\n";\n    } else if (fs::is_regular_file(path)) {\n        std::cout << path << " is a regular file\n";\n    } else {\n        std::cout << path << " is something else\n";\n    }\n\n    return 0;\n}