#include <fstream>\n#include <string>\n\nint main() {\n    std::ifstream file("example.txt");\n    if (!file) {\n        // handle error\n    }\n\n    int startLine = 10; // line X\n    int endLine = 20;   // line Y\n    int currentLine = 0;\n\n    std::string line;\n    while (std::getline(file, line)) {\n        currentLine++;\n        if (currentLine >= startLine && currentLine <= endLine) {\n            // process the line\n            std::cout << line << std::endl;\n        }\n    }\n\n    file.close();\n    return 0;\n}