#include <iostream>\n#include <string>\n#include <bitset>\n\nint main() {\n    // Decimal to hexadecimal\n    int decimal = 123;\n    std::cout << "Decimal: " << decimal << std::endl;\n    std::cout << "Hexadecimal: " << std::hex << decimal << std::endl;\n\n    // Hexadecimal to decimal\n    std::string hexadecimal = "7B";\n    int decimalFromHex = std::stoi(hexadecimal, 0, 16);\n    std::cout << "Hexadecimal: " << hexadecimal << std::endl;\n    std::cout << "Decimal: " << decimalFromHex << std::endl;\n\n    // Binary to decimal\n    std::string binary = "1111011";\n    int decimalFromBinary = std::stoi(binary, 0, 2);\n    std::cout << "Binary: " << binary << std::endl;\n    std::cout << "Decimal: " << decimalFromBinary << std::endl;\n\n    // Decimal to binary\n    decimal = 123;\n    std::bitset<8> binaryFromDecimal(decimal);\n    std::cout << "Decimal: " << decimal << std::endl;\n    std::cout << "Binary: " << binaryFromDecimal << std::endl;\n\n    return 0;\n}