#include <Windows.h>\n\nint main() {\n    SC_HANDLE hSCManager = OpenSCManager(NULL, NULL, SC_MANAGER_CONNECT);\n    if (hSCManager == NULL) {\n        // handle error\n    }\n\n    SC_HANDLE hService = CreateService(hSCManager, "ServiceName", "ServiceName", SERVICE_ALL_ACCESS, SERVICE_WIN32_OWN_PROCESS, SERVICE_AUTO_START, SERVICE_ERROR_IGNORE, "C:\\Path\\To\\ExeName.exe", NULL, NULL, NULL, NULL, NULL);\n    if (hService == NULL) {\n        // handle error\n    }\n\n    CloseServiceHandle(hService);\n    CloseServiceHandle(hSCManager);\n\n    return 0;\n}